package com.example.dogyubin.doubletwo;import android.content.Intent;import android.graphics.drawable.Drawable;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.LinearLayout;import com.bumptech.glide.Glide;import com.bumptech.glide.request.target.SimpleTarget;import com.bumptech.glide.request.transition.Transition;public class StartActivity extends AppCompatActivity {    LinearLayout start_layout;    Button start_signup_button, start_login_button;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_start);        start_layout = findViewById(R.id.start_layout);        start_login_button = findViewById(R.id.start_login_button);        start_signup_button = findViewById(R.id.start_signup_button);        start_signup_button.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(StartActivity.this, SignUpActivity.class);                startActivity(intent);            }        });        start_login_button.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {            }        });        Glide.with(this).load(R.drawable.ic_start_background).into(new SimpleTarget<Drawable>() {            @Override            public void onResourceReady(Drawable resource, Transition<? super Drawable> transition) {                start_layout.setBackground(resource);            }        });    }}